client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/components/FaceCapture.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import * as faceapi from "face-api.js";

interface FaceCaptureProps {
  mode: "enroll" | "verify";
  onFaceCapture?: (descriptor: Float32Array) => void;
  onVerificationResult?: (isMatch: boolean, confidence: number) => void;
  onStatusChange?: (status: 'idle' | 'detecting' | 'captured' | 'error') => void;
  existingDescriptor?: number[] | null;
  className?: string;
}

export default function FaceCapture({ 
  mode, 
  onFaceCapture, 
  onVerificationResult, 
  onStatusChange,
  existingDescriptor,
  className = "" 
}: FaceCaptureProps) {
  const [isLoading, setIsLoading] = useState(false);
  const [isModelLoaded, setIsModelLoaded] = useState(false);
  const [isVideoStarted, setIsVideoStarted] = useState(false);
  const [faceDetected, setFaceDetected] = useState(false);
  const [captureStatus, setCaptureStatus] = useState<'idle' | 'detecting' | 'captured' | 'error'>('idle');
  const [enrollmentTriggered, setEnrollmentTriggered] = useState(false);
  const [verificationTriggered, setVerificationTriggered] = useState(false);
  const [detectionQuality, setDetectionQuality] = useState(0);
  const [stableDetectionCount, setStableDetectionCount] = useState(0);

  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const detectionIntervalRef = useRef<number | null>(null);
  const stableCountRef = useRef<number>(0);

  const { toast } = useToast();

  // Load face-api.js models
  useEffect(() => {
    const loadModels = async () => {
      try {
        console.log('Starting to load face-api.js models...');
        setIsLoading(true);
        setIsModelLoaded(false);

        // Use CDN for models since we don't have local models set up
        const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';

        await Promise.all([
          faceapi.nets.ssdMobilenetv1.loadFromUri(MODEL_URL),
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        ]);

        console.log('Face-api.js models loaded successfully');
        setIsModelLoaded(true);
      } catch (error) {
        console.error('Error loading face-api.js models:', error);
        setIsModelLoaded(false);
        toast({
          title: "Model Loading Failed",
          description: "Failed to load face recognition models. Please refresh and try again.",
          variant: "destructive",
        });
      } finally {
        console.log('Finished loading models, setting isLoading to false');
        setIsLoading(false);
      }
    };

    loadModels();
  }, [toast]);

  // Watch for status changes and notify parent
  useEffect(() => {
    if (onStatusChange) {
      onStatusChange(captureStatus);
    }
  }, [captureStatus, onStatusChange]);

  // Start camera
  const startCamera = async () => {
    try {
      console.log('Starting camera - Models loaded:', isModelLoaded);

      if (!isModelLoaded) {
        toast({
          title: "Models Not Ready",
          description: "Please wait for face recognition models to load.",
          variant: "destructive",
        });
        return;
      }

      // Check if getUserMedia is available
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Camera API not supported in this browser');
      }

      // Check for WebGL support (required by face-api.js)
      const canvas = document.createElement('canvas');
      const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
      if (!gl) {
        throw new Error('WebGL not supported - required for face recognition');
      }

      console.log('Requesting camera access...');
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 1280, min: 640 },
          height: { ideal: 720, min: 480 },
          facingMode: 'user',
          frameRate: { ideal: 30, min: 15 }
        }
      });

      console.log('Camera access granted, setting up video stream');

      if (!videoRef.current) {
        console.error('Video ref is null');
        throw new Error('Video element not available');
      }

      const video = videoRef.current;
      console.log('Video element found:', video);

      // Set up video properties
      video.srcObject = stream;
      video.autoplay = true;
      video.playsInline = true;
      video.muted = true;

      streamRef.current = stream;
      console.log('Stream assigned to video');

      // Handle video events
      const handleLoadedMetadata = () => {
        console.log('Video metadata loaded:', {
          videoWidth: video.videoWidth,
          videoHeight: video.videoHeight,
          readyState: video.readyState
        });
        setIsVideoStarted(true);
        setCaptureStatus('detecting');
        setEnrollmentTriggered(false); // Reset enrollment flag when video starts
        setVerificationTriggered(false); // Reset verification flag when video starts
        setStableDetectionCount(0); // Reset stable count when video starts
        stableCountRef.current = 0; // Also reset the ref

        // Start face detection after a short delay to ensure video is playing
        setTimeout(() => {
          startFaceDetection();
        }, 500);
      };

      const handleCanPlay = () => {
        console.log('Video can play');
      };

      const handlePlaying = () => {
        console.log('Video is playing');
      };

      const handleError = (e: any) => {
        console.error('Video error event:', e);
        setCaptureStatus('error');
      };

      // Remove existing event listeners to avoid duplicates
      video.removeEventListener('loadedmetadata', handleLoadedMetadata);
      video.removeEventListener('canplay', handleCanPlay);
      video.removeEventListener('playing', handlePlaying);
      video.removeEventListener('error', handleError);

      // Add event listeners
      video.addEventListener('loadedmetadata', handleLoadedMetadata);
      video.addEventListener('canplay', handleCanPlay);
      video.addEventListener('playing', handlePlaying);
      video.addEventListener('error', handleError);

      // Manually trigger play
      try {
        await video.play();
        console.log('Video play() succeeded');
      } catch (error) {
        console.error('Video play() failed:', error);
        // Try to set video started anyway in case autoplay works
        setIsVideoStarted(true);
        setCaptureStatus('detecting');
      }

      console.log('Video stream setup complete');
    } catch (error: any) {
      console.error('Error accessing camera:', error);

      let errorMessage = "Please allow camera access to use face recognition.";
      let errorTitle = "Camera Access Denied";

      if (error.name === 'NotAllowedError') {
        errorMessage = "Camera access was denied. Please allow camera permissions and try again.";
      } else if (error.name === 'NotFoundError') {
        errorMessage = "No camera found. Please connect a camera and try again.";
        errorTitle = "Camera Not Found";
      } else if (error.name === 'NotSupportedError') {
        errorMessage = "Camera is not supported in this browser or requires HTTPS.";
        errorTitle = "Camera Not Supported";
      } else if (error.message.includes('not supported')) {
        errorMessage = "Camera API not supported. Try using Chrome, Firefox, or Safari.";
        errorTitle = "Browser Not Supported";
      } else {
        errorMessage = `Camera error: ${error.message}`;
        errorTitle = "Camera Error";
      }

      toast({
        title: errorTitle,
        description: errorMessage,
        variant: "destructive",
      });
      setCaptureStatus('error');
    }
  };

  // Start face detection loop
  const startFaceDetection = () => {
    console.log('Starting face detection...');
    if (!videoRef.current || !canvasRef.current) {
      console.log('Missing video or canvas ref');
      return;
    }

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    console.log('Video dimensions:', {
      videoWidth: video.videoWidth,
      videoHeight: video.videoHeight,
      readyState: video.readyState
    });

    // Set canvas dimensions to match video
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const detectFaces = async () => {
      if (!video || video.ended) {
        console.log('Video not available or ended');
        return;
      }

      // If video is paused, try to resume it
      if (video.paused) {
        console.log('Video is paused, attempting to resume...');
        try {
          await video.play();
          console.log('Video resumed successfully');
        } catch (error) {
          console.log('Failed to resume video:', error);
          return;
        }
      }

      try {
        console.log('Running face detection...');

        // Multi-tier detection strategy for better reliability
        let detections: any[] = [];
        let detectionAttempts = 0;
        const maxAttempts = 3;

        // Try multiple detection configurations with different sensitivities
        const detectionConfigs = [
          { 
            name: 'TinyFaceDetector_High',
            options: new faceapi.TinyFaceDetectorOptions({ inputSize: 416, scoreThreshold: 0.25 })
          },
          { 
            name: 'TinyFaceDetector_Medium',
            options: new faceapi.TinyFaceDetectorOptions({ inputSize: 320, scoreThreshold: 0.35 })
          },
          { 
            name: 'SsdMobilenet_Sensitive',
            options: new faceapi.SsdMobilenetv1Options({ minConfidence: 0.25 })
          }
        ];

        for (const config of detectionConfigs) {
          if (detections.length > 0) break;

          try {
            // console.log(`Trying ${config.name}...`);
            detections = await faceapi
              .detectAllFaces(video, config.options)
              .withFaceLandmarks()
              .withFaceDescriptors();

            if (detections.length > 0) {
              console.log(`${config.name} found ${detections.length} faces with confidence: ${detections[0].detection.score}`);
              break;
            }
          } catch (error) {
            console.log(`${config.name} failed:`, error);
          }

          detectionAttempts++;
        }

        // Clear canvas
        if (ctx) {
          ctx.clearRect(0, 0, canvas.width, canvas.height);
        }

        if (detections.length > 0) {
          const bestDetection = detections.reduce((best, current) => 
            current.detection.score > best.detection.score ? current : best
          );

          const confidence = bestDetection.detection.score;
          const minConfidenceThreshold = 0.4;

          if (confidence >= minConfidenceThreshold) {
            setFaceDetected(true);
            setDetectionQuality(confidence);

            // Use ref for immediate tracking and state for UI
            stableCountRef.current += 1;
            setStableDetectionCount(stableCountRef.current);

            console.log(`High quality face detected - confidence: ${confidence.toFixed(3)}, stable count: ${stableCountRef.current}`);

            // Draw detection boxes with color based on quality
            detections.forEach(detection => {
              const { x, y, width, height } = detection.detection.box;
              if (ctx) {
                const detectionConf = detection.detection.score;
                const quality = detectionConf >= 0.6 ? 'excellent' : detectionConf >= 0.5 ? 'good' : 'fair';

                // Color based on quality
                ctx.strokeStyle = detectionConf >= 0.6 ? '#00ff00' : detectionConf >= 0.5 ? '#ffff00' : '#ffa500';
                ctx.lineWidth = 3;
                ctx.strokeRect(x, y, width, height);

                // Draw confidence and quality
                ctx.fillStyle = ctx.strokeStyle;
                ctx.font = 'bold 16px Arial';
                ctx.fillText(
                  `${Math.round(detectionConf * 100)}% (${quality})`,
                  x, y - 10
                );
              }
            });

            // Require stable detection for reliable capture (at least 3 consecutive good detections)
            const requiredStability = mode === 'enroll' ? 5 : 3;
            if (stableCountRef.current >= requiredStability) {
              const detection = bestDetection;

              if (mode === "enroll" && onFaceCapture && !enrollmentTriggered) {
                console.log(` Triggering face enrollment after ${stableCountRef.current} stable detections`);
                setEnrollmentTriggered(true);
                onFaceCapture(detection.descriptor);
                setCaptureStatus('captured');
                stopCamera();
              } else if (mode === "verify" && onVerificationResult && existingDescriptor && !verificationTriggered) {
                // For verification, compare with existing descriptor (only once)
                console.log(" Triggering face verification - threshold reached!");
                setVerificationTriggered(true);

                const distance = faceapi.euclideanDistance(
                  detection.descriptor,
                  new Float32Array(existingDescriptor)
                );

                // Lower distance means better match (threshold ~0.6 is typical)
                const isMatch = distance < 0.6;
                const confidence = Math.max(0, (1 - distance) * 100);

                console.log(" Face verification result:", { isMatch, confidence: confidence.toFixed(1), distance: distance.toFixed(3) });

                onVerificationResult(isMatch, confidence);
                setCaptureStatus(isMatch ? 'captured' : 'error');
                stopCamera();
              }
            }
          } else {
            // Reset stable detection count if confidence is too low
            setFaceDetected(false);
            stableCountRef.current = 0;
            setStableDetectionCount(0);
            console.log(`Low confidence face detected: ${confidence.toFixed(3)} < ${minConfidenceThreshold}`);
          }
        } else {
          setFaceDetected(false);
          stableCountRef.current = 0;
          setStableDetectionCount(0);
          console.log('No face detected in current frame');

          // Draw "no face detected" message
          if (ctx) {
            ctx.fillStyle = '#ff0000';
            ctx.font = '18px Arial';
            ctx.fillText('No face detected', 10, 30);
          }
        }
      } catch (error) {
        console.error('Face detection error:', error);
      }
    };

    // Start detection loop with optimized timing
    console.log('Setting up detection interval...');
    detectionIntervalRef.current = window.setInterval(detectFaces, 150);
  };

  // Stop camera and cleanup
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }

    if (detectionIntervalRef.current) {
      clearInterval(detectionIntervalRef.current);
      detectionIntervalRef.current = null;
    }

    setIsVideoStarted(false);
    setFaceDetected(false);
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopCamera();
    };
  }, []);

  const getStatusMessage = () => {
    switch (captureStatus) {
      case 'idle':
        return mode === 'enroll' ? 'Ready to capture your face for registration' : 'Ready to verify your identity';
      case 'detecting':
        if (faceDetected) {
          const qualityText = detectionQuality >= 0.6 ? 'excellent' : detectionQuality >= 0.5 ? 'good' : 'fair';
          const requiredStability = mode === 'enroll' ? 5 : 3;
          return `Face detected (${qualityText} quality)! Hold still... ${stableDetectionCount}/${requiredStability} stable frames`;
        }
        return 'Please position your face in the camera view';
      case 'captured':
        return mode === 'enroll' ? 'Face captured successfully!' : 'Face verified successfully!';
      case 'error':
        return mode === 'verify' ? 'Face verification failed' : 'Face capture failed';
      default:
        return '';
    }
  };

  const getStatusColor = () => {
    switch (captureStatus) {
      case 'detecting':
        return faceDetected ? 'text-warning' : 'text-muted-foreground';
      case 'captured':
        return 'text-success';
      case 'error':
        return 'text-destructive';
      default:
        return 'text-muted-foreground';
    }
  };

  return (
    <Card className={`border border-border ${className}`}>
      <CardContent className="p-6">
        <div className="text-center mb-4">
          <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-3">
            <i className="fas fa-camera text-primary text-xl"></i>
          </div>
          <h3 className="text-lg font-semibold text-foreground mb-2">
            {mode === 'enroll' ? 'Face Registration' : 'Face Verification'}
          </h3>
          <p className={`text-sm ${getStatusColor()}`}>
            {getStatusMessage()}
          </p>
        </div>

        <div 
          className="relative bg-gray-900 rounded-lg overflow-hidden mb-4" 
          style={{ width: '100%', height: '400px' }}
        >
          <video
            ref={videoRef}
            autoPlay
            playsInline
            muted
            className="w-full h-full object-cover"
            style={{ 
              width: '100%', 
              height: '100%',
              display: 'block',
              backgroundColor: '#000',
              position: 'relative',
              zIndex: 0
            }}
            data-testid="face-capture-video"
          />

          <canvas
            ref={canvasRef}
            className="absolute top-0 left-0 pointer-events-none"
            style={{ 
              width: '100%', 
              height: '100%',
              zIndex: 1,
              position: 'absolute'
            }}
            data-testid="face-capture-canvas"
          />

          {/* Status overlay */}
          <div className="absolute top-4 left-4 bg-black/80 text-white text-sm px-3 py-2 rounded-md z-20">
            <div className="flex items-center space-x-2">
              <div className={`w-2 h-2 rounded-full ${isVideoStarted ? 'bg-green-500' : 'bg-red-500'}`}></div>
              <span>{isVideoStarted ? 'Camera Active' : 'Camera Inactive'}</span>
            </div>
            <div className="text-xs opacity-75 mt-1">Status: {captureStatus}</div>
          </div>

          {/* Loading/Error overlay */}
          {!isVideoStarted && (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-900/90 z-10">
              {isLoading && !isModelLoaded ? (
                <div className="text-center text-white">
                  <i className="fas fa-spinner fa-spin text-3xl mb-3"></i>
                  <p className="text-lg">Loading face detection models...</p>
                  <p className="text-sm opacity-75">This may take a few moments</p>
                </div>
              ) : captureStatus === 'error' ? (
                <div className="text-center text-red-400">
                  <i className="fas fa-exclamation-triangle text-3xl mb-3"></i>
                  <p className="text-lg">Camera Error</p>
                  <p className="text-sm opacity-75">Check camera permissions</p>
                </div>
              ) : !isModelLoaded ? (
                <div className="text-center text-yellow-400">
                  <i className="fas fa-exclamation-triangle text-3xl mb-3"></i>
                  <p className="text-lg">Models Failed to Load</p>
                  <p className="text-sm opacity-75">Please refresh the page and try again</p>
                </div>
              ) : (
                <div className="text-center text-gray-400">
                  <i className="fas fa-video text-4xl mb-4"></i>
                  <p className="text-lg">Face detection ready</p>
                  <p className="text-sm opacity-75">Click "Start Camera" to begin</p>
                </div>
              )}
            </div>
          )}
        </div>

        <div className="flex items-center justify-center space-x-4">
          {!isVideoStarted ? (
            <Button
              onClick={startCamera}
              disabled={isLoading || !isModelLoaded}
              data-testid="button-start-camera"
            >
              <i className="fas fa-play mr-2"></i>
              Start Camera
            </Button>
          ) : (
            <Button
              onClick={stopCamera}
              variant="outline"
              data-testid="button-stop-camera"
            >
              <i className="fas fa-stop mr-2"></i>
              Stop Camera
            </Button>
          )}
        </div>

        {/* Instructions */}
        <div className="mt-4 p-4 bg-accent rounded-lg">
          <h4 className="text-sm font-semibold text-foreground mb-2">Instructions:</h4>
          <ul className="text-xs text-muted-foreground space-y-1">
            <li> Ensure good lighting for better detection</li>
            <li> Keep your face centered in the camera view</li>
            <li> Hold still when your face is detected</li>
            <li> Only one face should be visible in the camera</li>
          </ul>
        </div>
      </CardContent>
    </Card>
  );
}

